// AddMovie.js - Improved form handling and validation
import React, { useEffect, useState } from "react";
import { api } from "./config/api.config";
import { useFormik } from "formik";
import * as yup from "yup";
import {
  TextField,
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  CircularProgress,
  Alert
} from "@mui/material";

const movieValidationSchema = yup.object({
  name: yup.string().required("Name is required").min(3),
  desc: yup.string().required("Description is required").min(10),
  director: yup.string().required("Director is required").min(3),
  poster: yup.string().required("Poster URL is required").url("Must be a valid URL"),
  yearOfRelease: yup.number()
    .required("Year is required")
    .min(1888)
    .max(new Date().getFullYear() + 5),
  producer: yup.string().required("Producer is required"),
  actors: yup.array().min(1, "At least one actor is required")
});

function AddMovie() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [producers, setProducers] = useState([]);
  const [actors, setActors] = useState([]);

  useEffect(() => {
    loadInitialData();
  }, []);

  const loadInitialData = async () => {
    try {
      setLoading(true);
      const [producersRes, actorsRes] = await Promise.all([
        api.producers.getAll(),
        api.actors.getAll()
      ]);
      setProducers(producersRes.data);
      setActors(actorsRes.data);
    } catch (error) {
      setError(error.message || 'Failed to load initial data');
    } finally {
      setLoading(false);
    }
  };

  const formik = useFormik({
    initialValues: {
      name: "",
      desc: "",
      director: "",
      poster: "",
      yearOfRelease: "",
      producer: "",
      actors: []
    },
    validationSchema: movieValidationSchema,
    onSubmit: async (values) => {
      try {
        setLoading(true);
        await api.movies.add(values);
        navigate("/");
      } catch (error) {
        setError(error.message || 'Failed to add movie');
      } finally {
        setLoading(false);
      }
    }
  });

  if (loading) return <CircularProgress />;
  if (error) return <Alert severity="error">{error}</Alert>;

  return (
    <form onSubmit={formik.handleSubmit} className="formGroup">
      <TextField
        fullWidth
        label="Movie Name"
        {...formik.getFieldProps('name')}
        error={formik.touched.name && Boolean(formik.errors.name)}
        helperText={formik.touched.name && formik.errors.name}
      />
      {/* Add other form fields similarly */}
      <FormControl fullWidth>
        <InputLabel>Producer</InputLabel>
        <Select
          {...formik.getFieldProps('producer')}
          error={formik.touched.producer && Boolean(formik.errors.producer)}
        >
          {producers.map(producer => (
            <MenuItem key={producer._id} value={producer._id}>
              {producer.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      <FormControl fullWidth>
        <InputLabel>Actors</InputLabel>
        <Select
          multiple
          {...formik.getFieldProps('actors')}
          error={formik.touched.actors && Boolean(formik.errors.actors)}
        >
          {actors.map(actor => (
            <MenuItem key={actor._id} value={actor._id}>
              {actor.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      <Button 
        variant="contained" 
        type="submit" 
        disabled={loading}
        fullWidth
      >
        {loading ? <CircularProgress size={24} /> : 'Add Movie'}
      </Button>
    </form>
  );
}
