// Movie.js
import React, { useEffect, useState } from 'react';
import { makeAPICall } from './api.config';

const Movie = ({ setLoading, onError }) => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      setLoading(true);
      try {
        const data = await makeAPICall('/movies');
        setMovies(data);
      } catch (error) {
        onError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, [setLoading, onError]);

  return (
    <div>
      {movies.map(movie => (
        <div key={movie._id}>{movie.title}</div>
      ))}
    </div>
  );
};

export default Movie;
